package weekendDay1;

public class CheckNumberIsPositiveOrNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float i = 34.4f;
		if (i < 0)
			System.out.println("The given number " + i + " Negative");
		else if (i > 0)
			System.out.println("The given number " + i + " Positive");
		else
			System.out.println("The give number is zero");

	}

}
