package weekendDay1;

public class IsPrime {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 3;
		if (i % 2 == 1)
			System.out.println("The given input " + i + " is prime number");
		else
			System.out.println("The given input " + i + " is not prime number");
	}
}
